import classes from './Meals.module.css'

const DUMMY_MEALS = [
    {
      id: 'm1',
      name: 'Sushi',
      description: 'Finest fish and veggies',
      price: 22.99,
    },
    {
      id: 'm2',
      name: 'Schnitzel',
      description: 'A german specialty!',
      price: 16.5,
    },
    {
      id: 'm3',
      name: 'Barbecue Burger',
      description: 'American, raw, meaty',
      price: 12.99,
    },
    {
      id: 'm4',
      name: 'Green Bowl',
      description: 'Healthy...and green...',
      price: 18.99,
    },
  ];

const MealsSummary = () => {
    return (
      <section className={classes.summary}>
        <h2>Delicious Food, Delivered To You</h2>
        <p>
          Choose your favorite meal from our broad selection of available meals
          and enjoy a delicious lunch or dinner at home.
        </p>
        <p>
          All our meals are cooked with high-quality ingredients, just-in-time and
          of course by experienced chefs!
        </p>
      </section>
    );
  };

const AvailableMeals = () => {
    return (
        <section className={classes.meals}>
            <Card>
                <ul>
                    {DUMMY_MEALS.map(meal => <MealItem name={meal.name} description={meal.description} price={meal.price}/>)}
                </ul>
            </Card>
        </section>
    )
}

const MealItem = props => {
    const price = `$${props.price.toFixed(2)}`;

    return (
        <li className={classes.meal}>
            <div>
                <h3>{props.name}</h3>
                <div className={classes.description}>{props.description}</div>
                <div className={classes.price}>{price}</div>
            </div>
            <div>
                <Form />
            </div>
        </li>
    )
}

function add(event) {
    event.preventDefault()
    let value = event.target.parentNode.childNodes[0].childNodes[1].value;
    let price = Number(event.target.parentNode.parentNode.parentNode.childNodes[0].childNodes[2].textContent.substring(1));
    
}

const Form = props => {
    return (
        <form className={classes.form}>
            <Input label="Amount" input={{
                id: 'amount',
                type: 'number',
                min: '1',
                max: '5',
                step: '1',
                defaultValue: '1'
            }} />
            <button onClick={add}>+ Add</button>
        </form>
    )
}

const Input = props => {
    return (
        <div className={classes.input}>
            <label htmlFor={props.input.id}>{props.label}</label>
            <input id={props.input.id} {...props.input} />
        </div>
    )
}

const Card = (props) => {
    return (
        <div className={classes.card}>
            {props.children}
        </div>
    )
}

export default function Meals() {
    return (
        <>
            <MealsSummary />
            <AvailableMeals />
        </>
    )
}
